mapper.select:
SELECT t.*
FROM r_sys_res t


	
		ORDER BY t.sr_id desc
	
	
		ORDER BY ${order}
	


	LIMIT
	#{firstResult,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

